mapper.update:
UPDATE account_info
SET user_id = #{userId}, class_type = #{classType}, account = #{account}, password = #{password}, other_secret = #{otherSecret},
    bind_phone = #{bindPhone}, bind_email = #{bindEmail}, bind_answer = #{bindAnswer}, bind_ask = #{bindAsk}, creation_time = #{creationTime},
    update_time = #{updateTime}, flag = #{flag}
WHERE id = #{id}
